Phaserfroot.PluginManager.register(
  "hearts_gfbjs",
  class hearts_gfbjs extends Phaserfroot.Component {
    constructor( target, instanceProperties ) {
      super( {
        name: "hearts_gfbjs",
        owner: target,
      } );
      this.instanceProperties = instanceProperties;
      this.scene = target.scene;
      this.game = target.scene.game;

      this.owner.once( "levelSwitch", this.destroy, this );

      // Attach custom event listeners.
      this.owner.on( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );
      this.owner.properties.onUpdate( this.onMessageReceived, this, "_messaging_");


      // Initialize properties from parameters.
      this.player = ( typeof instanceProperties[ "player" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "player" ], true ) : null;
      this.instance = ( typeof instanceProperties[ "instance" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "instance" ], true ) : null;
      this.hearts = instanceProperties[ "hearts" ];


      // Boot phase.
      this.camera = this.scene.cameras.main;

      this.onCreate();

    }

    // BUILT-IN METHODS

    preUpdate () {

    }

    update () {

    }

    postUpdate () {
      this.EVENTS_POST_UPDATE();

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      this.owner.removeListener( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );
      if ( this.delayed_event ) this.delayed_event.remove();

    }

    // CUSTOM METHODS

    onCreate () {
      // Executed when this script is initially created.
      this.owner.setPhysics( false );
    }

    executeMessageend () {
      // Executed when the message 'end' is received.
      this.owner.visible = false;
    }

    executeMessagegame_over () {
      // Executed when the message 'game over' is received.
      this.owner.visible = false;
    }

    EVENTS_POST_UPDATE () {
      // Executed every frame.
      if (this.owner.tags.has( 'master heart' )) {
        var i_end = this.hearts.length;
        var i_inc = 1;
        if (1 > i_end) {
          i_inc = -i_inc;
        }
        for (var i = 1;
             i_inc >= 0 ? i <= i_end : i >= i_end;
             i += i_inc) {
          if ( !(this.hearts[i - 1]) ) {
            this.reportError( "`Set Instance X` block could not find the instance [(this.hearts[i - 1])]." );
            return;
          }
          (this.hearts[i - 1]).posX = this.camera.posX + (i * 50 - 40);
          if ( !(this.hearts[i - 1]) ) {
            this.reportError( "`Set Instance Y` block could not find the instance [(this.hearts[i - 1])]." );
            return;
          }
          (this.hearts[i - 1]).posY = this.camera.posY + 10;
        }
      }
    }

    reportError( message ) {
      message = "(" + this.name.replace( /_[\d]+$/, "" ) + ")" + message;
      console.trace( message );
      this.game.reportError( message, message, "SCRIPT ERROR" );
    }

    errorCheckNotNull( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    onLevelStart2() {
      this.player = this.scene.getChildrenByTag( 'player' )[ 0 ];
      this.owner.tags.add( 'master heart' );
      this.hearts = [];
      if ( !this.hearts ) {
        this.reportError( "`Add to List` block could not find a list called [hearts]." );
        return;
      }
      this.hearts.push( this.owner );
      var repeat_end = (this.errorCheckNotNull( this.player, this.owner, "`Get Key on Instance` block could not find an instance called [player].")).properties.get( 'max-health' ) - 1;
      for (var count = 0; count < repeat_end; count++) {
        var instance = this.scene.addSpriteByName( this.owner.name );
        if ( !instance ) {
          this.reportError( "`Change Instance Depth` block could not find the instance [instance]." );
          return;
        }
        this.scene.addChildBefore( instance, this.owner );
        if ( !this.hearts ) {
          this.reportError( "`Add to List` block could not find a list called [hearts]." );
          return;
        }
        this.hearts.push( instance );
      }
      this.evaluateHealth(  );

    }

    errorCheckNotNull2( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    errorCheckNotNull3( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    errorCheckNotNull4( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    evaluateHealth (  ) {
      var instance_list = this.hearts;
      for (var instance_index in instance_list) {
        this.instance = instance_list[instance_index];
        if ( !this.instance ) {
          this.reportError( "`Set Instance Visibility` block could not find the instance [instance]." );
          return;
        }
        this.instance.visible = false;
      }
      if ((this.errorCheckNotNull2( this.player, this.owner, "`Get Key on Instance` block could not find an instance called [player].")).properties.get( 'health' ) != 0) {
        var j_end = ((this.errorCheckNotNull3( this.player, this.owner, "`Get Key on Instance` block could not find an instance called [player].")).properties.get( 'health' ) / (this.errorCheckNotNull4( this.player, this.owner, "`Get Key on Instance` block could not find an instance called [player].")).properties.get( 'max-health' )) * this.hearts.length;
        var j_inc = 1;
        if (1 > j_end) {
          j_inc = -j_inc;
        }
        for (var j = 1;
             j_inc >= 0 ? j <= j_end : j >= j_end;
             j += j_inc) {
          if ( !(this.hearts[j - 1]) ) {
            this.reportError( "`Set Instance Visibility` block could not find the instance [(this.hearts[j - 1])]." );
            return;
          }
          (this.hearts[j - 1]).visible = true;
        }
      }
      this.delayed_event = this.scene.time.delayedCall( 100, function() {
        if ( !this.owner || this.owner.exists === false ) {
          return;
        }
          this.evaluateHealth(  );
      }, null, this );
    }

    onMessageReceived ( name, message ) {

      if ( message === 'end' ) {
        this.executeMessageend();
      }

      if ( message === 'game over' ) {
        this.executeMessagegame_over();
      }

    }

  }
);

Phaserfroot.PluginManager.register(
  "dizzy_stars_gfbjs",
  class dizzy_stars_gfbjs extends Phaserfroot.Component {
    constructor( target, instanceProperties ) {
      super( {
        name: "dizzy_stars_gfbjs",
        owner: target,
      } );
      this.instanceProperties = instanceProperties;
      this.scene = target.scene;
      this.game = target.scene.game;

      this.owner.once( "levelSwitch", this.destroy, this );

      // Attach custom event listeners.
      this.owner.on( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );
      this.owner.properties.onUpdate( this.onMessageReceived, this, "_messaging_");


      // Initialize properties from parameters.
      this.value = ( typeof instanceProperties[ "value" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "value" ], true ) : null;
      this.master = instanceProperties[ "master" ];


      // Boot phase.

      this.onCreate();

    }

    // BUILT-IN METHODS

    preUpdate () {

    }

    update () {

    }

    postUpdate () {
      this.EVENTS_POST_UPDATE();

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      this.owner.removeListener( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );

    }

    // CUSTOM METHODS

    onCreate () {
      // Executed when this script is initially created.
      this.owner.tags.add( 'stars' );
      this.owner.setPhysics( false );
      if (!this.master) {
        this.owner.visible = true;
      }
      this.owner.anchorX = this.owner.width / 2;
      this.owner.anchorY = this.owner.height / 2;
    }

    executeMessagedizzy () {
      // Executed when the 'dizzy' is received.
      if (this.master) {
        var instance = this.scene.addSpriteByName( this.owner.name );
        if ( !instance ) {
          this.reportError( "`Set Instance X` block could not find the instance [instance]." );
          return;
        }
        instance.posX = (this.errorCheckNotNull( this.value, this.owner, "`Get Position of Instance` block could not find an instance named [value].")).posX;
        if ( !instance ) {
          this.reportError( "`Set Instance Y` block could not find the instance [instance]." );
          return;
        }
        instance.posY = (this.errorCheckNotNull2( this.value, this.owner, "`Get Position of Instance` block could not find an instance named [value].")).posY - 30;
        if ( !instance ) {
          this.reportError( "`Set Key` block could not find the instance [instance]." );
          return;
        }
        instance.properties.set( 'dizzy character',this.value );
      }
    }

    executeMessageundizzy () {
      // Executed when the 'undizzy' is received.
      if (!this.master) {
        if (this.owner.properties.get( 'dizzy character' ) == this.value) {
          this.owner.destroySafe();
        }
      }
    }

    EVENTS_POST_UPDATE () {
      // Executed every frame.
      if (!this.master) {
        this.owner.bearing = this.owner.bearing + 5;
      }
    }

    onLevelStart2() {
      this.master = true;
      this.owner.visible = false;

    }

    onMessageReceived ( name, message ) {

      if ( message === 'dizzy' ) {
        this.value = this.owner.properties.get( "_messaging-value_" );
        this.executeMessagedizzy();
      }

      if ( message === 'undizzy' ) {
        this.value = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageundizzy();
      }

    }

    reportError( message ) {
      message = "(" + this.name.replace( /_[\d]+$/, "" ) + ")" + message;
      console.trace( message );
      this.game.reportError( message, message, "SCRIPT ERROR" );
    }

    errorCheckNotNull( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    errorCheckNotNull2( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

  }
);

Phaserfroot.PluginManager.register(
  "Wall_Slide_Sprite_gfbjs",
  class Wall_Slide_Sprite_gfbjs extends Phaserfroot.Component {
    constructor( target, instanceProperties ) {
      super( {
        name: "Wall_Slide_Sprite_gfbjs",
        owner: target,
      } );
      this.instanceProperties = instanceProperties;
      this.scene = target.scene;
      this.game = target.scene.game;

      this.owner.once( "levelSwitch", this.destroy, this );

      // Attach custom event listeners.
      this.owner.properties.onUpdate( this.onMessageReceived, this, "_messaging_");


      // Initialize properties from parameters.
      this.spawnLocation = instanceProperties[ "spawnLocation" ];


      // Boot phase.

      this.onCreate();

    }

    // BUILT-IN METHODS

    preUpdate () {

    }

    update () {

    }

    postUpdate () {

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      if ( this.delayed_event ) this.delayed_event.remove();

    }

    // CUSTOM METHODS

    onCreate () {
      // Executed when this script is initially created.
      this.owner.tags.add( 'wallSlideSprite' );
      this.owner.setPhysics( false );
      this.owner.anchorX = this.owner.width / 2;
      this.owner.anchorY = this.owner.height / 2;
      this.owner.visible = false;
    }

    executeMessageGO () {
      // Executed when the 'GO' is received.
      this.owner.setPhysics( true );

      // Set side collisions.
      this.owner.body.checkCollision.left = false;
      this.owner.body.checkCollision.right = false;
      this.owner.body.checkCollision.up = false;
      this.owner.body.checkCollision.down = false;
      this.owner.body.checkCollision.none = !(
        this.owner.body.checkCollision.left ||
        this.owner.body.checkCollision.right ||
        this.owner.body.checkCollision.up ||
        this.owner.body.checkCollision.down );
      // Get the current layer.
      var layer = this.scene.physicsLayersManager.getLayersWithChild( this.owner )[ 0 ];
      layer = layer ? this.scene.physicsLayersManager.layers.indexOf( layer ) : 0;
      if ( this.owner.body.checkCollision.none ) {
        // Remove from current layer.
        this.scene.physicsLayersManager.layers[ layer ].remove( this.owner );
      } else if ( !this.scene.physicsLayersManager.layers[ layer ].hasChild( this.owner ) ) {
      // Add back to a layer - probably default, 0.
        this.scene.physicsLayersManager.layers[ layer ].add( this.owner );
      }

      this.owner.body.acceleration.y = 1000;
      this.owner.body.velocity.x = (this.math_random_int( -100, 100 ));
      this.owner.body.velocity.y = (this.math_random_int( -100, 100 ));
      this.owner.posX = (this.errorCheckNotNull( this.spawnLocation, { x: 0, y: 0 }, "`Get X/Y of Location` block could not find a location named [spawnLocation].")).x - this.owner.anchorX;
      this.owner.posY = (this.errorCheckNotNull2( this.spawnLocation, { x: 0, y: 0 }, "`Get X/Y of Location` block could not find a location named [spawnLocation].")).y - this.owner.anchorY;
      this.owner.bearing = this.math_random_int( 1, 360 );
      this.owner.scaleX = 0.2;
      this.owner.scaleY = 0.2;
      this.owner.visible = true;
      this.owner.alpha = Math.random() * 0.5;
      this.delayed_event = this.scene.time.delayedCall( this.math_random_int( 200, 400 ), function() {
        if ( !this.owner || this.owner.exists === false ) {
          return;
        }
          this.owner.destroySafe();
      }, null, this );
    }

    onMessageReceived ( name, message ) {

      if ( message === 'GO' ) {
        this.spawnLocation = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageGO();
      }

    }

    math_random_int ( a, b ) {
      if ( a > b ) {
        // Swap a and b to ensure a is smaller.
        var c = a;
        a = b;
        b = c;
      }
      return Math.floor( Math.random() * ( b - a + 1 ) + a );
    }

    reportError( message ) {
      message = "(" + this.name.replace( /_[\d]+$/, "" ) + ")" + message;
      console.trace( message );
      this.game.reportError( message, message, "SCRIPT ERROR" );
    }

    errorCheckNotNull( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    errorCheckNotNull2( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

  }
);

Phaserfroot.PluginManager.register(
  "slingshot_ball_gfbjs",
  class slingshot_ball_gfbjs extends Phaserfroot.Component {
    constructor( target, instanceProperties ) {
      super( {
        name: "slingshot_ball_gfbjs",
        owner: target,
      } );
      this.instanceProperties = instanceProperties;
      this.scene = target.scene;
      this.game = target.scene.game;

      this.owner.once( "levelSwitch", this.destroy, this );

      // Attach custom event listeners.
      this.owner.on( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );
      this.owner.properties.onUpdate( this.onMessageReceived, this, "_messaging_");
      this.owner.on( this.owner.EVENTS.COLLIDE, this.onTouchInstance2, this );


      // Initialize properties from parameters.
      this.value = instanceProperties[ "value" ];
      this.toucher = ( typeof instanceProperties[ "toucher" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "toucher" ], true ) : null;
      this.master = instanceProperties[ "master" ];


      // Boot phase.

      this.onCreate();

    }

    // BUILT-IN METHODS

    preUpdate () {

    }

    update () {

    }

    postUpdate () {

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      this.owner.removeListener( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );
      this.owner.off( this.owner.EVENTS.COLLIDE, this.onTouchInstance2, this );

    }

    // CUSTOM METHODS

    onCreate () {
      // Executed when this script is initially created.
      this.owner.tags.add( 'ball' );
    }

    executeMessageshoot () {
      // Executed when the 'shoot' is received.
      if (!this.master) {
        this.owner.setPhysics( true );
        this.owner.visible = true;
        if (this.value == 1) {
          this.owner.body.velocity.x = 1000;
        } else {
          this.owner.body.velocity.x = (-1000);
        }
        this.owner.body.velocity.y = (-100);
        this.owner.body.acceleration.y = 800;
      }
    }

    onLevelStart2() {
      this.master = true;
      this.owner.setPhysics( false );
      this.owner.visible = false;

    }

    onMessageReceived ( name, message ) {

      if ( message === 'shoot' ) {
        this.value = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageshoot();
      }

    }

    onTouchInstance2 ( instance ) {
      if ( !instance ) {
        return;
      }
      if ( instance instanceof Phaser.Tilemaps.Tile ) {
        instance = instance.layer.tilemapLayer;
      }
      this.toucher = instance;
        this.owner.destroySafe();

    }

  }
);

Phaserfroot.PluginManager.register(
  "Game_Over_gfbjs",
  class Game_Over_gfbjs extends Phaserfroot.Component {
    constructor( target, instanceProperties ) {
      super( {
        name: "Game_Over_gfbjs",
        owner: target,
      } );
      this.instanceProperties = instanceProperties;
      this.scene = target.scene;
      this.game = target.scene.game;

      this.owner.once( "levelSwitch", this.destroy, this );

      // Attach custom event listeners.
      this.owner.on( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );
      this.owner.properties.onUpdate( this.onMessageReceived, this, "_messaging_");


      // Initialize properties from parameters.
      this.paper_bottom_slide_offset = instanceProperties[ "paper bottom slide offset" ];
      this.paper_top_slide_offset = instanceProperties[ "paper top slide offset" ];
      this.white_fader = ( typeof instanceProperties[ "white fader" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "white fader" ], true ) : null;
      this.black_fader = ( typeof instanceProperties[ "black fader" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "black fader" ], true ) : null;
      this.game_over_banner = ( typeof instanceProperties[ "game over banner" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "game over banner" ], true ) : null;
      this.paper_top = ( typeof instanceProperties[ "paper top" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "paper top" ], true ) : null;
      this.paper_bottom = ( typeof instanceProperties[ "paper bottom" ] !== "undefined" ) ? this.scene.getChildById( instanceProperties[ "paper bottom" ], true ) : null;
      this.end1 = instanceProperties[ "end1" ];
      this.end2 = instanceProperties[ "end2" ];


      // Boot phase.
      this.camera = this.scene.cameras.main;

      this.onCreate();

    }

    // BUILT-IN METHODS

    preUpdate () {

    }

    update () {

    }

    postUpdate () {
      this.EVENTS_POST_UPDATE();

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      this.owner.removeListener( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );
      if ( this.delayed_event ) this.delayed_event.remove();
      if ( this.delayed_event2 ) this.delayed_event2.remove();
      if ( this.delayed_event3 ) this.delayed_event3.remove();
      if ( this.delayed_event4 ) this.delayed_event4.remove();
      if ( this.delayed_event5 ) this.delayed_event5.remove();
      if ( this.delayed_event6 ) this.delayed_event6.remove();

    }

    // CUSTOM METHODS

    onCreate () {
      // Executed when this script is initially created.
      this.owner.setPhysics( false );
      this.owner.visible = false;
      this.owner.anchorX = this.owner.width / 2;
      this.owner.anchorY = this.owner.height / 2;
      this.owner.scaleX = 1.5;
      this.owner.scaleY = 1.5;
    }

    executeMessagegame_over () {
      // Executed when the message 'game over' is received.
      if (!this.end1) {
        this.end1 = true;
        this.owner.visible = true;
      }
    }

    executeMessagePLAYER_DIE () {
      // Executed when the message 'PLAYER_DIE' is received.
      if (!this.end1) {
        this.end1 = true;
        this.owner.visible = true;
        this.create_black_fader(  );
        this.delayed_event3 = this.scene.time.delayedCall( 500, function() {
          if ( !this.owner || this.owner.exists === false ) {
            return;
          }
            if ( !this.paper_top ) {
            this.reportError( "`Set Instance Visibility` block could not find the instance [paper_top]." );
            return;
          }
          this.paper_top.visible = true;
          this.slide_down_top_paper(  );
          this.delayed_event2 = this.scene.time.delayedCall( 200, function() {
            if ( !this.owner || this.owner.exists === false ) {
              return;
            }
              if ( !this.game_over_banner ) {
              this.reportError( "`Set Instance Visibility` block could not find the instance [game_over_banner]." );
              return;
            }
            this.game_over_banner.visible = true;
            if ( !this.game_over_banner ) {
              this.reportError( "`Set Instance Visibility` block could not find the instance [game_over_banner]." );
              return;
            }
            this.game_over_banner.anchorX = this.game_over_banner.width / 2;
            this.game_over_banner.anchorY = this.game_over_banner.height / 2;
            if ( !this.game_over_banner ) {
              this.reportError( "`Set Instance Scale` block could not find the instance [game_over_banner]." );
              return;
            }
            this.game_over_banner.scaleX = 1.5;
            if ( !this.game_over_banner ) {
              this.reportError( "`Set Instance Scale` block could not find the instance [game_over_banner]." );
              return;
            }
            this.game_over_banner.scaleY = 1.5;
            if ( !this.game_over_banner ) {
              this.reportError( "`Set Instance Alpha` block could not find the instance [game_over_banner]." );
              return;
            }
            this.game_over_banner.alpha = 0;
            this.zoom_out_game_over(  );
            this.delayed_event = this.scene.time.delayedCall( 800, function() {
              if ( !this.owner || this.owner.exists === false ) {
                return;
              }
                if ( !this.paper_bottom ) {
                this.reportError( "`Set Instance Visibility` block could not find the instance [paper_bottom]." );
                return;
              }
              this.paper_bottom.visible = true;
              this.slide_up_bottom_paper(  );
            }, null, this );
          }, null, this );
        }, null, this );
      }
    }

    EVENTS_POST_UPDATE () {
      // Executed every frame.
      if (this.end1) {
        this.owner.posX = this.camera.posX;
        this.owner.posY = this.camera.posY;
        if ( !this.black_fader ) {
          this.reportError( "`Set Instance X` block could not find the instance [black_fader]." );
          return;
        }
        this.black_fader.posX = this.camera.posX;
        if ( !this.black_fader ) {
          this.reportError( "`Set Instance Y` block could not find the instance [black_fader]." );
          return;
        }
        this.black_fader.posY = this.camera.posY;
        if ( !this.black_fader ) {
          this.reportError( "`Set Instance Alpha` block could not find the instance [black_fader]." );
          return;
        }
        this.black_fader.alpha = (this.errorCheckNotNull( this.black_fader, this.owner, "`Get alpha of Instance` block could not find an instance named [black_fader].")).alpha - 0.01;
        this.owner.scaleX = Math.min(Math.max(this.owner.scaleX * 0.99, 1), 1.5);
        this.owner.scaleY = Math.min(Math.max(this.owner.scaleY * 0.99, 1), 1.5);
        if (this.end2) {
          if (this.owner.visible) {
            this.owner.alpha = this.owner.alpha - 0.07;
            if (this.owner.alpha == 0) {
              this.owner.visible = false;
            }
          }
        }
